import React, { Component } from 'react'
import {connect} from 'react-redux'

class PostForm extends Component{

    constructor() {
        super()
        this.state = {
            nb: 0,
        }
    }
    handleSubmit = (e) => {
        // if (isNaN(this.getPrenom.value)) {
        // //    var maVariable = 'Not a Number!'
        // //     document.getElementById("mavar").innerHTML = maVariable;
        // //     return(console.log('Not a Number!') );
        //     var erreur = document.getElementById('erreur')
        //     erreur.innerHTML = 'Entrer un nombre'
        //     console.log(erreur.innerHTML)
        // }else
        // {
            e.preventDefault();
            this.setState({
             nb : this.state.nb + 1
            })

            const nom = this.getNom.value;
            const prenom = this.getPrenom.value;
            const produit = nom.charAt(0).toUpperCase() + nom.substring(1).toLowerCase()

            const data = {
                id: new Date(),            
                nb : this.state.nb,
                produit,
                prenom,
                editing:false
            }
            this.props.dispatch({
                type:'ADD_POST',
                data
            });
            this.getNom.value = '';
            this.getPrenom.value = '';
        //}

    }
    render(){
        return(
            <center className={"form"} >
                <form onSubmit={event => {
                event.preventDefault()
                if (!this.getNom.value || !this.getPrenom.value) return

                if(isNaN(this.getPrenom.value)){
                    var err = document.getElementById('addErreur')
                    err.innerHTML = 'Entrer un nombre'
                }else{
                    {this.handleSubmit}
                }}} className={'nom'}>
                    <label>Produit</label>
                    <input required type='text' ref={(input)=>this.getNom = input} />
                    <label>Prix</label>
                    <input required type='text' ref={(input)=>this.getPrenom = input} />
                    <label>Ar</label>
                    <button className={'ajout btn btn-primary'}  >Ajouter</button>
                    <p id='erreur'>

                    </p>
                </form>
            </center>
        )
    }
}
export default connect()(PostForm)
