    return (
      <center className={"form"}>
      <form onSubmit={event => {
								event.preventDefault()
                //console.log(this.props.post.prenom);

                }}>
        <table> 

          <thead>
            <tr className={"active"}>
              <th>Id</th>
              <th>Produits</th>
              <th>Prix</th>
              <th>Actions </th>
            </tr>
          </thead>
          {this.props.posts.map((post) => <Post key={post.id} post={post} />)}

          {/* {this.props.posts.map((post) => (
            <div key={post.id}>
              {post.editing ? <EditComponent post={post} key={post.id} /> : <Post post={post}
          key={post.id} />}
            </div>
          ))} */}
        </table>
        <div  >
          <button className={'total btn btn-info'}
        onClick={() => {this.total() }}
                >TOTAL </button>

        </div>

        <div className={'ariary'}>
          TOTAL=       Ar
          {this.props.posts.map.id}
        </div>
      </form>
        </center>
    )}



}

const mapStateToProps = (state) => {
    return  {
        posts: state
    }
}
export default connect(mapStateToProps)(AllPost)
