import React, { Component } from 'react';
import { connect } from 'react-redux';
import Post from './Post';
class AllPost extends Component {


  total = () => {
    //var somme = 'this.props.post.prenom'
    //console.log(somme);
    this.props.posts.map((post) => <Conste key={post.id} post={post} />) 
    //console.log(c);

  }
  render() {
    return (
      <center className={"form"}>
      <form onSubmit={event => {
								event.preventDefault()
                //console.log(this.props.post.prenom);

                }}>
        <table> 

          <thead>
            <tr className={"active"}>
              <th>Id</th>
              <th>Produits</th>
              <th>Prix</th>
              <th>Actions </th>
            </tr>
          </thead>
          {this.props.posts.map((post) => <Post key={post.id} post={post} />)}
          {}
        </table>
        <div  >
          <button   className={'total btn btn-info'} 
          onClick={()=>{
            this.total()}}
          >TOTAL </button>

        </div>

        <div className={'ariary'}>
          TOTAL=  <b id='tot'>  
    {this.props.posts.map((post) => <Conste key={post.id} post={post} />)}  </b>   Ar
        </div>
      </form>
        </center>
    )}



}

class Conste extends Component{
  render() {
    return  (
      <div>
{
         document.getElementById('tot').innerHTML = this.props.post.mme
        //console.log(this.props.post.mme)}
}
      </div>

    )
    }


}

const mapStateToProps = (state) => {
    return  {
        posts: state
    }
}
export default connect(mapStateToProps)(AllPost)
