import React, { Component } from 'react'
import {connect} from 'react-redux'
import { confirmAlert } from 'react-confirm-alert'; 
import 'react-confirm-alert/src/react-confirm-alert.css'
class Post extends Component {

  submit = () => (
		confirmAlert({
			title: '',
			message: 'Confirmer la suppression',
			buttons: [
			  {
				label: 'oui',
				onClick: () =>this.props.dispatch({type:'DELETE_POST',id:this.props.post.id})
			  },
			  {
				label: 'Non',
				onClick: () => console.log("blabla")
			  }
			]
		  })
	)



	constructor() {
		super()
		this.state = {
				nb: 0,
		}
}
handleSubmit = (e) => {
		e.preventDefault();
		this.setState({
		 nb : this.state.nb + 1
		})

		const nom = this.getNom.value;
		const prenom = this.getPrenom.value;
		var data = {
				id: new Date(),            
				nb : this.state.nb,
				nom,
				prenom,
				editing:false
		}
		this.props.dispatch({
				type:'ADD_POST',
				data
		});
		this.getNom.value = '';
		this.getPrenom.value = '';
		alert(data)
}


  update = () => (
		confirmAlert({
			customUI: ({ onClose }) => {
				//var data = {this.props.post.prenom}

				//const data = this.newPrenom.value
				return (
					<div className="custom-ui">
						<input type='text' name='inp' ref={(input)=>this.newPrenom = input }defaultValue={this.props.post.prenom}  />

						<button onSubmit={this.handleSubmit}
							onClick={() => {
								//this.props.dispatch({type:'EDIT_POST',id:this.props.post.id });
								this.props.dispatch({ type: 'EDIT_POST', id: this.props.post.id,data:  this.newPrenom.value})
								//alert(data)
								onClose();
							}}
						>
							OUI
						</button>
						<button onClick={onClose}>NON</button>
					</div>
				);
			}
		})
	)
  render() {
  return (

		<tbody>
      <tr>
        <td>{this.props.post.nb}</td>
        <td>{this.props.post.nom}</td>
        <td>{this.props.post.prenom}</td>
        <td> 
					<button onClick={()=>this.submit() } className={"btn btn-danger"} >X</button>
					<button onClick={()=>this.update()}> Edit</button>
					{this.props.posts.map((post) => (

			<div key={post.id}>
				 {post.editing ? <EditComponent post={post} key={post.id} /> : <Post post={post}
    key={post.id} />}
			</div>
	))} 

					{/* <button
       onClick={()=>this.props.dispatch({type:'EDIT_POST',id:this.props.post.id})}>
       Edit</button> */}
					</td>        
      </tr>
		</tbody>




  );
 }
}

export default connect()(Post);
