import React, { Component } from 'react';
import { connect } from 'react-redux';
import Post from './Post';
class AllPost extends Component {


  total = () => {
    //var somme = 'this.props.post.prenom'
    //console.log(somme);
    this.props.posts.map((post) => <Conste key={post.id} post={post} />) 
  }
  render() {
    return (
      <center className={"form"}>
      <form onSubmit={event => {
								event.preventDefault()
                //console.log(this.props.post.prenom);

                }}>
        <table> 

          <thead>
            <tr className={"active"}>
              <th>Id</th>
              <th>Produits</th>
              <th>Prix</th>
              <th>Actions </th>
            </tr>
          </thead>
          {this.props.posts.map((post) => <Post key={post.id} post={post} />)}
        </table>
        <div  >
          <button   className={'total btn btn-info'} 

         onSubmit={this.total()}
        //   this.props.posts.map((post) => <Conste key={post.id} post={post} />)} 
        // }
                >TOTAL </button>

        </div>

        <div className={'ariary'}>
          TOTAL=   {this.somme()}   Ar
        </div>
      </form>
        </center>
    )}



}

class Conste extends Component{
  //var c = this.props.post.produit
  render() {
    return  (
      <div>
        {this.props.post.mme}
      </div>

    )
    }


}

const mapStateToProps = (state) => {
    return  {
        posts: state
    }
}
export default connect(mapStateToProps)(AllPost)
